var easyPub         = require("../../easyrtc/lib/easyrtc_public_obj"); 

var _           = require("../../easyrtc/node_modules/underscore/underscore");

/**
 * override method
 * @param event 
 * @param listener
 */
exports.on = function(event, listener) {
    if (event && _.isFunction(listener)) {
    	easyPub.events.removeAllListeners(event);
    	easyPub.events.on(event, listener);
    }else{
    	easyPub.util.logError("Unable to add listener to event '" + event + "'");
    }
};
/**
 * override method
 * @param event 
 * @param listener
 */
exports.addListener = function(event, listener) {
    if (event && _.isFunction(listener)) {
    	easyPub.events.removeAllListeners(event);
    	easyPub.events.on(event, listener);
    }else{
    	easyPub.util.logError("Unable to add listener to event '" + event + "'");
    }
};


/**
 * override method
 * @param event 
 * @param args
 */
exports.emit = function(event, args) {
	easyPub.events.emit("event", args);
};














