var log4js = require("../../log4js"); 
log4js.configure({
	  "appenders": [
	    	{ type: 'console' }, 		//控制台
		    {
			"type": "dateFile",
			"filename": "logs/log.log",
			"pattern": "-yyyy-MM-dd.log",
			"alwaysIncludePattern": false,
			"maxLogSize": 204800,
			"backups": 15
		    }
    	]
	});


module.exports = log4js;






