//easy public object
var easyPub         = require("../../easyrtc/lib/easyrtc_public_obj"); 

/**
 * Logging "Debug" level items.
 * @param       {string} logText        Text for log.
 * @param       {?*} [logFields]        Simple JSON object which contains extra fields to be logged.
 */
exports.debug = function(logText, logFields){
	easyPub.util.logDebug(logText, logFields);
};
/**
 * Logging "Info" level items.
 * @param       {string} logText        Text for log.
 * @param       {?*} [logFields]        Simple JSON object which contains extra fields to be logged.
 */
exports.info = function(logText, logFields){
	easyPub.util.logInfo(logText, logFields);
};
/**
 * Logging "Warn" level items.
 * @param       {string} logText        Text for log.
 * @param       {?*} [logFields]        Simple JSON object which contains extra fields to be logged.
 */
exports.warn = function(logText, logFields){
	easyPub.util.logWarning(logText, logFields);
};
/**
 * Logging "Error" level items.
 * @param       {string} logText        Text for log.
 * @param       {?*} [logFields]        Simple JSON object which contains extra fields to be logged.
 */
exports.error = function(logText, logFields){
	easyPub.util.logError(logText, logFields);
};

